package com.sandeepbegudem.demo.batch.processing.entity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@AllArgsConstructor@NoArgsConstructor@Data@Entity@Table(name = "customers")public class Customer {    @Id    @Column(name = "customer_id")    private int id;    @Column(name = "first_name")    private String firstName;    @Column(name = "last_name")    private String lastName;    @Column(name = "email")    private String email;    @Column(name = "gender")    private String gender;    @Column(name = "contact_number")    private String contactNo;    @Column(name = "country")    private String country;    @Column(name = "dob")    private  String dob;}